package GQT_Total_codes;
class AreaOfRhombusUtil {
    public static double arearhombus(double d1, double d2) {
        return (d1*d2)/2;
    }
}
public class Static15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 System.out.println("Area of Rhombus = " + AreaOfRhombusUtil.arearhombus(10,15));
	}

}
