package GQT_Total_codes;
class AreaOfTrapezoidUtil {
    public static double areatrapezoid(double base1,double base2, double hei) {
        return ((base1+base2)/2)*hei;
    }
}
public class Static17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 System.out.println("Area of Rhombus = " + AreaOfTrapezoidUtil.areatrapezoid(10,20,15));
	}

}
