package GQT_Total_codes;
class AreaOfRegularPolygonUtil {
    public static double arearpolygon(double base, double hei) {
        return (base*hei)/2;
    }
}
public class Static18 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 System.out.println("Area of Regular polygon = " + AreaOfRegularPolygonUtil.arearpolygon(10,15));
	}

}
